#compliance#
[
  {
    "clause_id": "asme-pressure-relief",
    "action": "Create a Pressure Relief Valve (PRV) sizing procedure requiring ASME Section VIII Division 1 calculations, set, capacity, and certified drawings for each PRV; ensure calculations are peer reviewed and validated against API 520 test data where applicable, with records retained per the Document Control procedure."
  },
  {
    "clause_id": "asme-material-traceability",
    "action": "Implement a material traceability procedure requiring MTCs/MTRs and serial/lot tracking for all load-bearing components, with CMMS tagging, a central log for heat numbers and inspection status, and random audits to verify chain-of-custody and record retention."
  },
  {
    "clause_id": "iec-grounding",
    "action": "Adopt a grounding/bonding procedure aligned with IEC 60204-1, including cabinet earth terminals, protective conductor sizing, torque specifications, continuity verification, and label requirements; require test records before energization."
  },
  {
    "clause_id": "iec-cable-identification",
    "action": "Establish a cable identification standard requiring unique, cross-referenced identifiers at both terminations and within panels, aligned with IEC 60204-1 Clause 13; enforce through pre-handover checks and a wiring verification log."
  },
  {
    "clause_id": "iso-safety-integrity",
    "action": "Develop a functional safety workflow per ISO 13849-1 requiring preliminary hazard analysis, SIL/PLr allocation, subsystem MTTFd/DCavg/CCF calculations, verification reports, and validation documentation prior to deployment."
  },
  {
    "clause_id": "iec-software-versioning",
    "action": "Adopt a firmware versioning policy using semantic versioning with release notes, version tags, traceability to build artifacts, and documented rollback procedures; require configuration baselines and release approval in the CCB."
  },
  {
    "clause_id": "pm-change-control",
    "action": "Stand up a Change Control Board with defined gates, roles, criteria, and risk/impact assessment; require signed ECNs and release notes, and ensure customer notifications and version control for all customer-facing revisions."
  }
]
#compliance#
