#compliance#
{
  "document_id": 1,
  "remediation_actions": [
    {
      "clause_id": "asme-pressure-relief",
      "action": "Review pressure relief valve sizing calculations against ASME Section VIII Div 1 requirements. Update specification to include certified engineer verification step.",
      "responsible": "Mechanical Engineering Lead",
      "verification": "ASME-stamped calculation package in design file"
    },
    {
      "clause_id": "asme-material-traceability",
      "action": "Implement material certification tracking system with unique heat numbers for all structural components. Update QA procedure QAP-102 to require MTRs with shipment.",
      "responsible": "Quality Manager",
      "verification": "Material traceability log with cross-reference to purchase orders"
    },
    {
      "clause_id": "iec-grounding",
      "action": "Perform grounding continuity tests on all control cabinets using 25A test current per IEC 60204-1. Update electrical schematics to show protective earth bonding points.",
      "responsible": "Electrical Engineer",
      "verification": "Test records showing <0.1Ω resistance between PE terminal and cabinet structure"
    },
    {
      "clause_id": "iec-cable-identification",
      "action": "Implement wire labeling protocol per IEC 60204-1 clause 13.2. Require ferrule labels at both ends for all conductors >0.5mm².",
      "responsible": "Installation Supervisor",
      "verification": "Pre-shipment inspection checklist item #45 for wire identification"
    },
    {
      "clause_id": "iso-safety-integrity",
      "action": "Conduct PLr/PLc calculations per ISO 13849-1 for all safety functions. Document results in FMEA report appendix B.",
      "responsible": "Safety Engineer",
      "verification": "Signed PL calculation sheets with component MTBF data sources"
    },
    {
      "clause_id": "iec-software-versioning",
      "action": "Adopt semantic versioning (MAJOR.MINOR.PATCH) in firmware builds. Implement dual-bank flash architecture for rollback capability.",
      "responsible": "Software Architect",
      "verification": "Version control history showing rollback procedure validation in test environment"
    },
    {
      "clause_id": "pm-change-control",
      "action": "Establish Change Control Board with representatives from Engineering, QA, and Customer Support. Update ECO process to require CCB sign-off before release.",
      "responsible": "Project Manager",
      "verification": "CCB meeting minutes with approval signatures for all revisions"
    }
  ]
}
#compliance#
